INSERT INTO `books_content` (`book_id`, `sort_no`, `content`) VALUES

(book_id_change, 1, 'المقدمة'),
(book_id_change, 2, 'يَقُـولُ رَاجـِي عَفْـوِ رَبٍّ سَـامِعِ        مُحَـمَّـدُ بْـنُ الْجَـزَرِىِّ الشَّافِـعِي'),
(book_id_change, 3, 'الْـحَمْــدُ لِلَّـهِ وَصَـلَّـى اللّـَهُ        عَـلَـى نَبِـيِّـهِ وَمُـصْطــَفَـاهُ'),
(book_id_change, 4, 'مُـحَـمَّـدٍ وَآلِــهِ وَصَـحْـبِـه        وَمُقْـرِئِ الْقُــرْآنِ مَـعْ مُحـِـبِّه'),
(book_id_change, 5, 'وَبَـعْــدُ إِنَّ هَــذِهِ مُـقَـدِّمَـه        فيـماَ عَـلَـى قَـارِئِـهِ أَنْ يَعْلَـمهْ'),
(book_id_change, 6, 'إذْ وَاجِـبٌ عَلَـيْـهِـمُ مُحَـتّــمُ        قَـبْـلَ الشُـرُوعِ أَوَّلاً أَنْ يَعْلـَمُوا'),
(book_id_change, 7, 'مَـخَارِجَ الْحُـرُوفِ وَالـصِّـفَاتِ        لِـيَـلْفِـظُوا بِأَفْـصَــحِ اللُغَـاتِ'),
(book_id_change, 8, 'مُحَررِي التَّـجْـوِيـدِ وَالمَوَاقِـف        وَما الَّـذِي رُسِّـمَ في المَصـاَحِـفِ'),
(book_id_change, 9, 'مِنْ كُـلِّ مَقْطُوعٍ وَمَوْصـُولٍ بِـهَا        وَتَـاءِ أُنْـثَى لَمْ تَـكُنْ تُكْتَـبْ بِها'),
(book_id_change, 10, 'مخارج الحروف'),
(book_id_change, 11, 'مَخَارِجُ الحُروفِ سَبْعَةَ عَشَرْ        عَلَى الْذِي يَخْتَارُهُ مَنِ اخْتَبَرْ'),
(book_id_change, 12, 'فَأَلِفُ الجَوُفِ وأُخْتَاهَا وَهى        حُرُوفُ مَدٍّ للْـهَوَاءِ تَنْتَهـي'),
(book_id_change, 13, 'ثُمَّ لأَقْصَى الحَلْقِ هَمْزٌ هَاءُ        ثُمَّ لِـوَسْطِـهِ فَعَـيْنٌ حَـاءُ'),
(book_id_change, 14, 'أَدْنَـاهُ غَيْنٌ خَاؤُهَا والْقَـافُ        أَقْصَى اللِّسَانِ فَوْقُ ثُمَّ الْكَافُ'),
(book_id_change, 15, 'أَسْفَلُ وَالوَسْطُ فَجِيمُ الشِّينُ يـَا        وَالضَّادُ مِنْ خَافَتِهِ إِذْ وَلِيَــا'),
(book_id_change, 16, 'اَلأضْرَاسَ مِنْ أَيْسَرَ أَوْ يُمْنَاهَا        وَالـلاَّمُ أَدْنـَاهَا لمُنْتَهَاهــَا'),
(book_id_change, 17, 'وَالنُّونُ مِنْ طَرَفِهِ تَحْتُ اجْعَلُوا        وَالرَّا يُدَانِيهِ لِظَهْرٍ أَدْخــَلُوا'),
(book_id_change, 18, 'وَالطَّاءُ وَالدَّالُ وَتَا مِنْـهُ وَمِنْ        عُلْيَا الثَّنَايَا والصَّفِيرُ مُسْتـَكِنْ'),
(book_id_change, 19, 'مِنْهُ وَمِنْ فَوْقِ الثَّنَايَا السُّفْلَـى        وَالظـَّاءُ وَالذَّالُ وَثَا لِلْعُلْيــَا'),
(book_id_change, 20, 'مِنْ طَرْفَيْهِما وَمِنْ بَطْنِ الشَّفَهْ        فَالْفَا مَعَ اطْرافِ الثَّنَايَا المُشْرِفَهْ'),
(book_id_change, 21, 'للشَّـفَتَيْنِ الْـوَاوُ بَـاءٌ مِيـمُ        وَغُنَّـةٌ مَخْرَجُهـَا الخَيْشـُومُ'),
(book_id_change, 22, 'صفات الحروف'),
(book_id_change, 23, 'صِفَـاتُهَا جَهْـرٌ وَرِخْوٌ مُسْتَفِلْ        مُنْفَتِـحٌ مُصْمَـتَةٌ وَالضِّـدَّ قُـلْ'),
(book_id_change, 24, 'مَهْمُوسُهَا (فَحَثّهُ شَخْصٌ سَكَتَ)        شَدِيدُهَـا لَـفْظُ (أَجِدْ قَطٍ بَكَـتْ)'),
(book_id_change, 25, 'وَبَيْنَ رِخْوٍ وَالشَّدِيدِ ( لِنْ عُمَرْ)        وَسَبْعُ عُلْوٍ خُصَّ ضَغْطٍ قظْ حَصَرْ'),
(book_id_change, 26, 'وَصَادُ ضَادٌ طَاءُ ظَاءٌ مُطْـبَقَه        وَفَـرَّ مِنْ لُبِّ الحُـرُوفُ المُذْلَقَهْ'),
(book_id_change, 27, 'صَفِيـرُهَا صَـادٌ وَزَاىٌ سِينُ        قَلْـقَلَـةٌ قُـطْـبُ جَـدٍّ وَاللِّـينُ'),
(book_id_change, 28, 'وَاوٌ وَيَـاءٌ سَكَنَـا وَانْفَتَحَـا        قَبْلَـهُماَ وَالاِنْحِـرَافُ صُـحَّـحَا'),
(book_id_change, 29, 'في اللاًَّمِ وَالرَّا وَبِتَكْرِيرٍ جُعلْ        وَلِلتَّفَشِّي الشِّيـنُ ضَاداً اسْتَـطِـلْ'),
(book_id_change, 30, 'التجويد'),
(book_id_change, 31, 'وَالأَخْذُ بِالتَّجْوِيدِ حَتْـمٌ لاَزِمُ        مَنْ لَمْ يُجَوْدِ الْقُـرَآنَ آثِــمُ'),
(book_id_change, 32, 'لأَنَّهُ بِهِ الإِلَــهُ أَنْــزَلاَ        وَهَكَـذَا مِنْـهُ إِلَيْنَا وَصَـلاَ'),
(book_id_change, 33, 'وَهُوَ أَيْضاً حِلْـَيةُ الـتِّلاَوَةِ        وَزِينَـةُ الأَدَاءِ وَالْقِــرَاءَةِ'),
(book_id_change, 34, 'وَهُوَ إِعطْاءُ الْحُرُوفِ حَقَّهَا        مِنْ صِـفَةٍ لَـهَا وَمُستَحَقَّهَـا'),
(book_id_change, 35, 'وَرَدُّ كُلِّ وَاحِـدٍ لأَصلِـهِ        وَاللَّفْـظُ فِي نَظِيرِهِ كَمِثـْلهِ'),
(book_id_change, 36, 'مُكَمِّلاً مِنْ غَيْرِ مَا تَكَلُـفِ        بِاللُطْفِ فِي النُّطْقِ بِلاَ تَعَسُّف'),
(book_id_change, 37, 'وَلَيْـسَ بَيْنَـهُ وَبَيْنَ تَرْكِهِ        إِلاَّ رِيَـاضَةُ امْـرِئٍ بِفَكِّـه'),
(book_id_change, 38, 'التفخيم والترقيق'),
(book_id_change, 39, 'فَرقَّقَنْ مُسْتَفِلاً مِـنْ أَحْـرُفِ        وَحَاذِرَنْ تَفْخيِمَ لَفْـظِ الأَلِفِ'),
(book_id_change, 40, 'كَهَمْزِ أَلْحَمْدُ أَعُوذُ إِهْــدِنَا        أللَّهَ ثُــمَّ لاَمَ لِلَّهِ لَنَــا'),
(book_id_change, 41, 'وَلْيَتَلَطَّفْ وَعَلَى اللَّهِ وَلاَ الضْ        وَالمِيمِ مِنْ مَخْمَصَةٍ وَمِنْ مَرَضْ'),
(book_id_change, 42, 'وَبَاءَ بَرْقٍ بَاطِلٍ بِهِمْ بِذِي        وَاحْرِصْ عَلَى الشِّدَّةِ وَالجَهْرِ الَّذِي'),
(book_id_change, 43, 'فِيهَا وَفِى الْجِيِمِ كَحُبِّ الصَّبْرِ        ورَبْوَةٍ اجْتُثَّتْ وَحَجِّ الْفـَجْرِ'),
(book_id_change, 44, 'وَبَيِّنَنْ مُقَلْقَلاً إِنْ سَكَنَا        وَإِنْ يَكُنْ فِي الْوَقْفِ كَانَ أَبْيَـنَا'),
(book_id_change, 45, 'وَحَاءَ حَصْحَصَ أَحَطْتُ الْحَقُّ        وَسِينَ مُسْتَقِيمِ يَسْطُوا يَسْقُوا'),
(book_id_change, 46, 'الراءات'),
(book_id_change, 47, 'وَرَقِّقِ الرَّاءَ إِذَا مَا كُسِرَتْ        كَذَاكَ بَعْدَ الْكَسْرِ حَيْثُ سـَكَنَتْ'),
(book_id_change, 48, 'إِن لَّمْ تَكُنْ مِنْ قَبْلِ حَرْفِ اسْتِعْلاَ        أَوْ كـَانَت الْكَسْرَةُ لَيْسَتْ أَصْلاَ'),
(book_id_change, 49, 'وَالْخُلْفُ فِي فِرْقٍ لِكَسْرٍ يُوجَدُ        وَأَخْفِ تَكْرِيراً إِذَا تـُشَدَّدُ'),
(book_id_change, 50, 'اللامات'),
(book_id_change, 51, 'وَفَخِّم اللاَّمَ مِنِ اسْمِ اللَّهِ        عَنْ فَتْحٍ او ضَمٍ كَعَبْدُ اللَّـهِ'),
(book_id_change, 52, 'وَحَرْفَ الاِسْتِعْلاَءِ فَخِّمِ وَاخْصُصَا        الاِطْبَاقَ أَقْوَى نَحْوُ قَالَ وَالْعَصـَا'),
(book_id_change, 53, 'وَبَيِّنِ الإِطْبَاقَ مِنْ أَحَطتُ مَعْ        بَسَطتَ وَالخُلْفُ بِنَخْلُقكُّمْ وَقـَعْ'),
(book_id_change, 54, 'وَاحْرِصْ علَىَ السُّكُونِ فِي جَعَلْنَا        أَنْعَمْتَ وَالمَغْضُوبِ مَعْ ضـَلَلْنَا'),
(book_id_change, 55, 'وَخَلِّصِ انْفِتَاحَ مَحْذُوراً عَسَى        خَوْفَ اشْتِبَاهِهِ بِمَحْظُوراً عَصـَى'),
(book_id_change, 56, 'وَرَاعِ شِدَّةً بِكَافٍ وَبَتَا        كَشِرْكِكُمْ وَتَتَوَفَّى فِتْنَتـَا'),
(book_id_change, 57, 'وَأَوَّلَىْ مِثْلٍ وَجِنْسٍ إنْ سَكَنْ        أَدْغِمْ كَقُل رَّبِّ وَبَلَ لاَ وَأَبِـنْ'),
(book_id_change, 58, 'فِي يَوْمِ مَعْ قَالُوا وَهُمْ وَقُلْ نَعَمْ        سَبِّحْهُ لاَ تُزِغْ قُلُوبَ فَالْتَقـَمْ'),
(book_id_change, 59, 'الضاد والظاء'),
(book_id_change, 60, 'وَالضَّادَ بِاسْتِطَالَةٍ وَمـَخْرَجِ        مَيِّزْ مِنَ الظَّاءِ وَكُلُّهَا تَجِي'),
(book_id_change, 61, 'في الظَّعْنِ ظِلَّ الظُهْرِ عُظْمِ الْحِفْظِ        أَيْقَظْ وَانْظُرْ عَظْمِ ظَهْرِ اللَّفْظِ'),
(book_id_change, 62, 'ظَاهِرْ لَظَى شُوَاظِ كَظْمٍ ظَلَمَا        أُغْلُظْ ظَلامَ ظُفْرٍ انْتَظِرْ ظَمَا'),
(book_id_change, 63, 'أَظْفَرَ ظَنَّاً كَيْفَ جَا وَعَظْ سِوَى        عِضِينَ ظَلَّ النَّحْلُ زُخْرُفٍ سَوَا'),
(book_id_change, 64, 'وَظَلْتُ ظَلْتُمْ وَبِرُومٍ ظَلُّوا        كَالْحِجُرِ ظَلَّتْ شُعَرَا نَظَلُّ'),
(book_id_change, 65, 'يَظْلَلْنَ مَحْظُورَاً مَعَ المُحْتَظِر        وَكُنْتَ فَظَّاً وَجَمِيعٍ النَّظـَرِ'),
(book_id_change, 66, 'إِلاَّ بِوَيْلٌ هَلْ و أَولَى نَاضِرَهْ        وَالْغَيْظِ لاَ الرَّعْدِ وَهُودٍ قَـاصِرَهْ'),
(book_id_change, 67, 'وَالْحَظُّ لاَ الْحَضُّ عَلَى الطَّعَامِ        وَفي ضَنِينٍ الْخلاَفُ سَامِي'),
(book_id_change, 68, 'التحذيرات'),
(book_id_change, 69, 'وَإِنْ تَــلاَقَــيَا البَــيَانُ لاَزِمُ        أَنْـقَـضَ ظَـهْـرَكَ يَـعَضُّ الظَّـالِمُ'),
(book_id_change, 70, 'وَاضْـطُـرَّ مَعْ وَعَظْتَ مَعْ أَفَضْـتُمُ        وَصَـفِّ هَــا جِـبَاهُـهُـم عَلَيْهِمُ'),
(book_id_change, 71, 'الميم والنون المشددتين والميم الساكنة'),
(book_id_change, 72, 'وأَظْهِرِ اْلغُنَّةَ مِنْ نُونٍ وَمِنْ        مِيمٍ إِذاَ مَا شُدِّدَا وَأَخْفِيَنْ'),
(book_id_change, 73, 'الْمِيمَ إِنْ تَسْكُنْ بِغُنَّةٍ لَدَى        بَاءٍ عَلَى المُخْتَارِ مِنْ أَهْلِ اْلأَدَا'),
(book_id_change, 74, 'وَاظْهِرَنْهَا عِنْدَ بَاقِي اْلأَحْرُفِ        وَاحْذَرْ لَدى وَاوٍ وَفَا أنْ تَخْتَفِي'),
(book_id_change, 75, 'التنوين والنون الساكنة'),
(book_id_change, 76, 'وَحُكْمُ تَنْوِينٍ وَنُونٍ يُلْفى        إِظْهَارٌ ادْغَامٌ وَقَلبٌ اخْفــَا'),
(book_id_change, 77, 'فَعِنْدَ حَرْفِ الحَلْقِ أَظْهِرْ وَادَّغِمْ        فِي اللاَّمِ وَالرَّا لاَ بِغُنَّةٍ لَزِمْ'),
(book_id_change, 78, 'وَأَدْغِمَنْ بِغُنَّةٍ في يُومــِنُ        إِلاَّ بِكِلْمَــةٍ كَـدُنْيَا عَنْوَنُو'),
(book_id_change, 79, 'وَاْلَقْلبُ عِنْدَ الْبَا بِغُنَّةٍ كذا        لاِخْفَاء لَدَى بَاقِي الحُرُوفِ أُخِذَا'),
(book_id_change, 80, 'المد والقصر'),
(book_id_change, 81, 'والمدُّ لاَزِمٌ وَ وَاجِبٌ أَتَى        وَجَاَئزٌ وَهـْوَ وَ قَصــْرٌ ثَبَتَا'),
(book_id_change, 82, 'فَلاَزِمٌ إِن جَاءَ بَعْدَ حَرْفِ مَدْ        سَاكِنَ حَالَيْنِ وَبِالطُّولِ يُمـَدْ'),
(book_id_change, 83, 'وَوَاجِبٌ إنْ جاءَ قَبْلَ هَمْزَةِ        مُتَّصِلاً إِنْ جُمِعــَا بِكِلْمَـةِ'),
(book_id_change, 84, 'وَجَائزٌ إِذَا أَتَى مُنْفَصِلاَ        أَوْعَرَضَ السُّكُونُ وَقْفاٌ مُسْجَلاَ'),
(book_id_change, 85, 'معرفة الوقوف'),
(book_id_change, 86, 'وَبَـعْدَ تَجْوِيـدِكَ لِلْحُرُوفِ        لاَبُدَّ مِنْ مَعْرِفَـةِ الْـوُقُـوفِ'),
(book_id_change, 87, 'وَالاْبِتِـدَاءِ وَهْـىَ تُقْسَـمُ إِذَنْ        ثَلاَثَةٌ تَامٌ وَكَـافٍ وَحَسَــنْ'),
(book_id_change, 88, 'وَهْـىَ لِمَا تَـمَّ فَإنْ لَّمْ يُوجَــدِ        تَعَلُق أَوْ كَانَ مَعْنَـى فَابْتَدى'),
(book_id_change, 89, 'فَالتَّامُ فَالْكَافِى وَ لَفْظــاً فَامْنَعَنْ        إِلاَّ رُؤُس الآىِ جَوِّزْ فَالحَسَنْ'),
