INSERT INTO `books_content` (`book_id`, `sort_no`, `content`) VALUES

(book_id_change, 1, 'يَقُــولُ رَاجِـي رَحمةِ الْغَـفُـورِ        دَوْمـاً سُلَـيْمَانُ هُـوَ الْجَمـْزُورِى'),
(book_id_change, 2, 'الْحَمْــدُ لِلَّــهِ مُصَـلّـِياً عَـلـى        مُـحَمَـــدٍ وآلِــهِ وَمَـنْ تَـلاَ'),
(book_id_change, 3, 'وَبَعْـدُ هـذَا النَّـظْـمُ لِلْـمُرِيــدِ        فــي النُـونِ والتَّـنْوِينِ وَالْمُدُودِ'),
(book_id_change, 4, 'سَــمَّيتُــهُ بِتُحفَـة الأَطْفَالِ        عَـنْ شَيْـخِنَا الْمَيْـهِىِّ ذِي الْكَمالِ'),
(book_id_change, 5, 'أَرْجُــو بِـه أَنْ يَنْـفَعَ الطُّـلاَّبَـا        وَالأَجْــرَ وَالْقَـبُـولَ وَالثَّـوَابـا'),
(book_id_change, 6, 'النون الساكنة والتنوين'),
(book_id_change, 7, 'لِلـنُّــونِ إِنْ تَسْـكُنْ وَلِلتّـَنْوِينِ        أَرْبَـعُ أَحْكَـامٍ فَخُـذْ تَبْـيِـيـنِـي'),
(book_id_change, 8, 'فَـالأَوَّلُ الإظْـهَارُ قَـبْـلَ أَحْـرُفِ        لِلْحَـلْـقِ سِـتٌ رُتِّبَتْ فَلـتَـعْرِفِ'),
(book_id_change, 9, 'هَمْـزٌ فَـهَـاءٌ ثُـمَّ عَـيْـنٌ حَـاءُ        مُـهْمَلَـتَانِ ثُــمَّ غَيْـنٌ خَــاءُ'),
(book_id_change, 10, 'والـثّـَانِ إِدْغَـامٌ بِسـتَّةٍ أَتَـتْ        فِـي يَـرْمَـلُـونَ عِنْدَهُمْ قَدْ ثَبَتَتْ'),
(book_id_change, 11, 'لَـكِنَّهَا قِسْـمَانِ قِسْــمٌ يُـدْغَـمَا        فِـيهِ بِـغُـنّـَةٍ بِيَـنْمُو عُلِـمَـا'),
(book_id_change, 12, 'إِلاَّ إِذَا كَـانَـا بِـكــِلْمَـةٍ فَــلاَ        تُـدْغِـمْ كَدُنْـيَا ثُمَّ صِـنْوَانٌ تَـلاَ'),
(book_id_change, 13, 'وَالثَّـانِ إِدْغَــامٌ بِغَيْــرِ غُـنَّةْ        فـي الـلاَّمِ وَالـرَّا ثُـمَّ كَـرّرَنَّـهْ'),
(book_id_change, 14, 'وَّالثَـالـثُ الإِقْـلاَبُ عِنْـدَ الْبَـاءِ        مِيــماً بِغُـنَــةٍ مَـعَ الإِخْـفَـاءِ'),
(book_id_change, 15, 'وَالرَّابِـعُ الإِخْـفَاءُ عِنْـدَ الْفاضِـلِ        مِـنَ الحُـرُوفِ وَاجِـبٌ لِلْفَاضِـلِ'),
(book_id_change, 16, 'فـي خَمْسَـةٍ مِنْ بَعْدِ عَشْرٍ رَمْزُهَا        فِـي كِلْمِ هذَا البَيْتِ قَـدْ ضَمَّنـْتُـهَا'),
(book_id_change, 17, 'صِفْ ذَا ثَـنَا كَمْ جَادَ شَخْصٌ قَدْ سمَا        دُمْ طَيّـَباً زِدْ فِي تُـقَىً ضَعْ ظَالِـمَا'),
(book_id_change, 18, 'وَغُـنَّ مِيـماً ثُـمَّ نُونـاً شُــدِّدَا        وَسَــمِّ كُـلاً حَـرْفَ غُـنَّةٍ بَـدَا'),
(book_id_change, 19, 'وَالِميـمُ إِنْ تَسْـكُنْ تَجِى قَبْلَ الْهِجَا        لاَ أَلِــفٍ لَيِّــنَةٍ لِــذِى الْحِـجَا'),
(book_id_change, 20, 'أَحْـكَامُـهَا ثَـلاَثَـةٌ لِمَـنْ ضَبَـطْ        إِخْـفَاءٌ ادْغَـامٌ وَإِظْـهَـارٌ فَقَــطْ'),
(book_id_change, 21, 'فَـالأَوَّلُ الإِخْـفَـاءُ عِنْـدَ الْبَــاءِ        وَسَـمِّـهِ الشَّفْــوِىَّ لِلْـقُــرَّاءِ'),
(book_id_change, 22, 'وَالثّـَانِ إِدْغَـامٌ بِمِـثْلِـهَا أَتَـى        وَسَـمِّ إدغـاماً صَـغـِيراً يَا فَـتَى'),
(book_id_change, 23, 'وَالثـَّالِـثُ الإِظْـهَارُ فِـى الْبَقِيَّـةْ        مِـنْ أَحْـرُفٍ وَسَـمِّـهَا شَفْوِيَّـهْ'),
(book_id_change, 24, 'وَاحْـذَرْ لَدَى وَاوٍ وَفَـا أَنْ تَخْتَـفىِ        لِـقُـرْبِــهَا وَلِاتحادِ فَاعْـرِفِ'),
(book_id_change, 25, 'لام آل ولام الفعل'),
(book_id_change, 26, 'لِلاَمِ أَلْ حَـالاَنِ قَـبْـلَ الْأَحْــرُفِ        أُولاَهُــمَا إِظْـهَـارُهَا فَلْتَـعْـرِفِ'),
(book_id_change, 27, 'قَبْلَ ارْبَـعٍ مَعْ عَشْرَةٍ خُـذْ عِلْمَـهُ        مِنْ إِبْـغِ حَجَّـكَ وَخَـفْ عَقِيـمَـهُ'),
(book_id_change, 28, 'ثَانِيـهِـمَا إِدْغَـامُـهَا فـى أَرْبَـعِ        وَعَـشْـرَةٍ أَيْـضاً وَرَمْـزَهَا فَـعِ'),
(book_id_change, 29, 'طِبْ ثُمَّ صِلْ رَحِماً تَفُـزْ ضِفْ ذَا نِعَمْ        دَعْ سُـوءَ ظَنٍ زُرْ شَرِيـفَاً لِلْكَـرَم'),
(book_id_change, 30, 'وَاللاَّمَ الاُولَـى سَـمِّـهَا قَمْـرِيَّـهْ        وَالـلاَّمَ الاُخْـرىَ سَمِّـهَا شَمْسِـيَّهْ'),
(book_id_change, 31, 'وأظْـهِـرَنَّ لاَمَ فِـعْــلٍ مُطْلَــقاً        فـى نَحْـوِ قُلْ نَعَمْ وَقُلْـنَا وَالْتَقَـى'),
(book_id_change, 32, 'إِنْ فِي الصِّفَاتِ وَالمَخَـارِجِ اتَّفَـقْ        حَـرْفَانِ فَالْمِثْـلاَنِ فِـيهِـمَا أَحَـقْ'),
(book_id_change, 33, 'وَإِنْ يَكُـونَا مَخْـرَجـاً تَقَـارَبَـا        وَفـي الصِّـفَاتِ اخْتَـلَـفَا يُلَقَّــبَا'),
(book_id_change, 34, 'مُـتْـقَارِبَـيْنِ أَوْ يَكُــونَا اتَّفَـقَا        فِـي مَـخْرَجٍ دُونَ الصِّفَاتِ حُقِّـقَا'),
(book_id_change, 35, 'بِالْمُتَجَانِسَــيْنِ ثُــمَّ إِنْ سَـكَـنْ        أَوَّلُ كُـلٍّ فَالصَّـغِـيرَ سَـمِّـيَـنْ'),
(book_id_change, 36, 'أَوْ حُـرِّكَ الْحَـرْفَـانِ فى كُلٍّ        فَقُـلْ كُـلٌّ كَبِــيرٌ وافْهَمَـنْـهُ بِالْمُـثُلْ'),
(book_id_change, 37, 'وَالمـَدُّ أَصْلِـىُّ وَ فَـرْعِــىٌّ لَـهُ        وَسَــمِّ أَوَّلاً طَبِيـعِـيّاً وَهُـــو'),
(book_id_change, 38, 'مَـالاَ تَوَقُّـفٌ لَـهُ عَـلـى سَـبَبْ        وَلابِـدُونِهِ الْحُروفُ تُجْـتَـلَـبْ'),
(book_id_change, 39, 'بلْ أَىُّ حَرْفٍ غَيْرِ هَمْزٍ أَوْ سُـكُونْ        جَا بَعْـدَ مَـدٍّ فَالطَّبِــيِعىَّ يَكُـونْ'),
(book_id_change, 40, 'وَالآخَرُ الْفَرْعِـىُّ مَوْقُـوفٌ عَلـى        سَـبَبْ كَهَمْزٍ أَوْ سُكُونٍ مُسْـجَـلاً'),
(book_id_change, 41, 'حُـرُوفُــهُ ثَـــلاَثَـةٌ فَعِـيـهَا        في لَفْـظِ وَاىٍ وَهْىَ فى نُوحِـيـهَا'),
(book_id_change, 42, 'وَالكَسْرُ قَبْـلَ الْيَا وَقَبْلَ الْواوِ ضَـمْ        شَـرْطٌ وَفَـتْحٌ قَبْـلَ أَلْفٍ يُلْتَــزَمْ'),
(book_id_change, 43, 'وَاللِّـينُ مِنْـهَا الْيَا وَوَاوٌ سُـكِـنَا        إِنِ انْفِــتَاحٌ قَبْـلَ كُـلٍّ أُعْـلِـنَا'),
(book_id_change, 44, 'لِلْمَــدِّ أَحْـكَـامٌ ثَـلاَثَـةٌ تَـدُومْ        وَهْـيَ الْوُجُوبُ وَالْجَوَازُ وَاللُّـزُومْ'),
(book_id_change, 45, 'فَـوَاجِبٌ إِنْ جَـاءَ هَمْـزٌ بَعْدَ مَـدْ        فِـي كِلْمَــةٍ وَذَا بِمُتَّصْــلْ يُعَـدْ'),
(book_id_change, 46, 'وَجَـائـزٌ مَـدٌ وَقَصْـرٌ إِنْ فُصِـل        كُـلٌّ بِكِلْمَــةٍ وَهَذَا المُـنْفَصِــلْ'),
(book_id_change, 47, 'وَمِثـْلُ ذَا إِنْ عَـرَضَ السُّـكُـونُ        وَقْـفَاً كَتَعْـلَـمُـونَ نَسْـتَعِــينُ'),
(book_id_change, 48, 'أَوْ قُـدِّمَ الْهَمْـزُ عَـلَـى المَـدِّ وَذَا        بَـدَلْ كَـآمَـنُوا وَإِيَـماناً خُــذَا'),
(book_id_change, 49, 'وَلاَزِمٌ إِنِ السُّـكُـونُ أُصِّــــلاَ        وَصْلاَ وَوَقْـفاً بَعْـدَ مَـدٍّ طُــوّلاَ'),
(book_id_change, 50, 'أَقْسَــامُ لاَزِمٍ لَـدَيْهِمْ أَرْبَـعَـةْ        وَتِـلْكَ كِـلْمِـيٌّ وَحَرْفِـيٌّ مَعَــهْ'),
(book_id_change, 51, 'كِـلاَهُـمَا مُـخَـفَّـفٌ مُـثَـقَّـلُ        فَـهَـــذِهِ أَرْبَـعَـةٌ تُـفَصَّــلُ'),
(book_id_change, 52, 'فَــــإِنْ بِـكِلْمَـةٍ سُـكُونٌ اجْـتَمَـعْ        مَـعْ حَرْفِ مَـدٍّ فَهْـــوَ كِلْمِـيٌّ وَقَـعْ'),
(book_id_change, 53, 'أَوْ فـي ثُـلاَثِـيِّ الْحُروفِ وُجِـدَا        وَالْمَـدُّ وَسْـطُـــهُ فَحَـرْفِــيٌّ بَـدَا'),
(book_id_change, 54, 'كِـلاَهُـمَا مُثَـــقَّـلٌ إِنْ أُدْغِـمَــا        مُخَفَفٌ كُــلُّ إِذَا لَـمْ يُـدْغَـمَـــــا'),
(book_id_change, 55, 'وَالـلاَّزِمُ الْحَـرْفِـيُّ أَوَّلَ السُّــوَرْ        وُجُـودُهُ وَفِـي ثَمَـانٍ انحَصَــرْ'),
(book_id_change, 56, 'يَـجْمَعُـهَا حُـرُوفُ كَمْ عَسَلْ نَقَصْ        وَعَيْنُ ذُو وَجْهَـيْـنِ والطُّولُ أَخَـصْ'),
(book_id_change, 57, 'وَمَا سِوَى الْحَرْفِ الثُّلاَثِي لاَ أَلِـفْ        فَـمَـدُّهُ مَـــدٌّ طَبِيـعِـيٌّ أُلِــــفْ'),
(book_id_change, 58, 'وَذَاكَ أَيْضـاً فِـي فَـوَاتِحِ السُّـوَرْ        فِي لَفْظِ حَيٌّ طَـاهِرٌ قَـدِ انْحَصَـرْ'),
(book_id_change, 59, 'وَيَجْمَـعُ الْفَوَاتِـحَ الأَرْبَـعْ عَشَـرْ        صِلْهُ سُحَيْراً مَنْ قَطَعْكَ ذَا اشْـتَهَرْ'),
(book_id_change, 60, 'الخاتمة'),
(book_id_change, 61, 'وَتَــمَّ ذَا النَّـظْمُ بِحَـمْدِ اللَّـــهِ        عَـلَـى تَمَامِـهِ بِـلاَ تَــنَاهِـى'),
(book_id_change, 62, 'أَبْـيَاتُـهُ نَـدٌّ بَـداَ لِـذِى النُّـهَى        تَارِيخُها بُشْـرَى لِمَـنْ يُـتْقِـنُـهَا'),
(book_id_change, 63, 'ثُـمَّ الصَّـلاَةُ وَالسَّــلاَمُ أَبَـــداَ        عِـلـى خِـتَامِ الأَنْبِـيَاءِ أَحْـمَـدَا'),
(book_id_change, 64, 'وَالآلِ وَالصَّــحْبِ وَكُـلِّ تَـابِـعِ        وَكُــلِّ قَـارِئٍ وكُـلِّ سَــامِـعِ');