(4023, 23, 456, 38, 'Ṣād', 'صٓ', 9, 9, 53, 'هَٰذَا مَا تُوعَدُونَ لِيَوۡمِ ٱلۡحِسَابِ ﰴ', 'هذا ما توعدون ليوم الحساب', '038053.mp3'),
(4024, 23, 456, 38, 'Ṣād', 'صٓ', 9, 10, 54, 'إِنَّ هَٰذَا لَرِزۡقُنَا مَا لَهُۥ مِن نَّفَادٍ ﰵ', 'إن هذا لرزقنا ما له من نفاد', '038054.mp3'),
(4025, 23, 456, 38, 'Ṣād', 'صٓ', 10, 10, 55, 'هَٰذَاۚ وَإِنَّ لِلطَّٰغِينَ لَشَرَّ مَـَٔابٖ ﰶ', 'هذا وإن للطاغين لشر مآب', '038055.mp3'),
(4026, 23, 456, 38, 'Ṣād', 'صٓ', 11, 11, 56, 'جَهَنَّمَ يَصۡلَوۡنَهَا فَبِئۡسَ ٱلۡمِهَادُ ﰷ', 'جهنم يصلونها فبئس المهاد', '038056.mp3'),
(4027, 23, 456, 38, 'Ṣād', 'صٓ', 11, 12, 57, 'هَٰذَا فَلۡيَذُوقُوهُ حَمِيمٞ وَغَسَّاقٞ ﰸ', 'هذا فليذوقوه حميم وغساق', '038057.mp3'),
(4028, 23, 456, 38, 'Ṣād', 'صٓ', 12, 12, 58, 'وَءَاخَرُ مِن شَكۡلِهِۦٓ أَزۡوَٰجٌ ﰹ', 'وآخر من شكله أزواج', '038058.mp3'),
(4029, 23, 456, 38, 'Ṣād', 'صٓ', 12, 13, 59, 'هَٰذَا فَوۡجٞ مُّقۡتَحِمٞ مَّعَكُمۡ لَا مَرۡحَبَۢا بِهِمۡۚ إِنَّهُمۡ صَالُواْ ٱلنَّارِ ﰺ', 'هذا فوج مقتحم معكم لا مرحبا بهم إنهم صالو النار', '038059.mp3'),
(4030, 23, 456, 38, 'Ṣād', 'صٓ', 13, 14, 60, 'قَالُواْ بَلۡ أَنتُمۡ لَا مَرۡحَبَۢا بِكُمۡۖ أَنتُمۡ قَدَّمۡتُمُوهُ لَنَاۖ فَبِئۡسَ ٱلۡقَرَارُ ﰻ', 'قالوا بل أنتم لا مرحبا بكم أنتم قدمتموه لنا فبئس القرار', '038060.mp3');
INSERT INTO `quran_hafs` (`id`, `jozz`, `page`, `sura_no`, `sura_name_en`, `sura_name_ar`, `line_start`, `line_end`, `aya_no`, `aya_text`, `aya_text_emlaey`, `sound`) VALUES
(4031, 23, 456, 38, 'Ṣād', 'صٓ', 15, 15, 61, 'قَالُواْ رَبَّنَا مَن قَدَّمَ لَنَا هَٰذَا فَزِدۡهُ عَذَابٗا ضِعۡفٗا فِي ٱلنَّارِ ﰼ', 'قالوا ربنا من قدم لنا هذا فزده عذابا ضعفا في النار', '038061.mp3'),
(4032, 23, 457, 38, 'Ṣād', 'صٓ', 1, 1, 62, 'وَقَالُواْ مَا لَنَا لَا نَرَىٰ رِجَالٗا كُنَّا نَعُدُّهُم مِّنَ ٱلۡأَشۡرَارِ ﰽ', 'وقالوا ما لنا لا نرى رجالا كنا نعدهم من الأشرار', '038062.mp3'),
(4033, 23, 457, 38, 'Ṣād', 'صٓ', 1, 2, 63, 'أَتَّخَذۡنَٰهُمۡ سِخۡرِيًّا أَمۡ زَاغَتۡ عَنۡهُمُ ٱلۡأَبۡصَٰرُ ﰾ', 'أتخذناهم سخريا أم زاغت عنهم الأبصار', '038063.mp3'),
(4034, 23, 457, 38, 'Ṣād', 'صٓ', 2, 3, 64, 'إِنَّ ذَٰلِكَ لَحَقّٞ تَخَاصُمُ أَهۡلِ ٱلنَّارِ ﰿ', 'إن ذلك لحق تخاصم أهل النار', '038064.mp3'),
(4035, 23, 457, 38, 'Ṣād', 'صٓ', 3, 3, 65, 'قُلۡ إِنَّمَآ أَنَا۠ مُنذِرٞۖ وَمَا مِنۡ إِلَٰهٍ إِلَّا ٱللَّهُ ٱلۡوَٰحِدُ ٱلۡقَهَّارُ ﱀ', 'قل إنما أنا منذر وما من إله إلا الله الواحد القهار', '038065.mp3'),
(4036, 23, 457, 38, 'Ṣād', 'صٓ', 4, 4, 66, 'رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا ٱلۡعَزِيزُ ٱلۡغَفَّٰرُ ﱁ', 'رب السموات والأرض وما بينهما العزيز الغفار', '038066.mp3'),
(4037, 23, 457, 38, 'Ṣād', 'صٓ', 4, 5, 67, 'قُلۡ هُوَ نَبَؤٌاْ عَظِيمٌ ﱂ', 'قل هو نبأ عظيم', '038067.mp3'),
(4038, 23, 457, 38, 'Ṣād', 'صٓ', 5, 5, 68, 'أَنتُمۡ عَنۡهُ مُعۡرِضُونَ ﱃ', 'أنتم عنه معرضون', '038068.mp3'),
(4039, 23, 457, 38, 'Ṣād', 'صٓ', 5, 6, 69, 'مَا كَانَ لِيَ مِنۡ عِلۡمِۭ بِٱلۡمَلَإِ ٱلۡأَعۡلَىٰٓ إِذۡ يَخۡتَصِمُونَ ﱄ', 'ما كان لي من علم بالملإ الأعلى إذ يختصمون', '038069.mp3'),
(4040, 23, 457, 38, 'Ṣād', 'صٓ', 6, 6, 70, 'إِن يُوحَىٰٓ إِلَيَّ إِلَّآ أَنَّمَآ أَنَا۠ نَذِيرٞ مُّبِينٌ ﱅ', 'إن يوحى إلي إلا أنما أنا نذير مبين', '038070.mp3'),
(4041, 23, 457, 38, 'Ṣād', 'صٓ', 6, 7, 71, 'إِذۡ قَالَ رَبُّكَ لِلۡمَلَٰٓئِكَةِ إِنِّي خَٰلِقُۢ بَشَرٗا مِّن طِينٖ ﱆ', 'إذ قال ربك للملائكة إني خالق بشرا من طين', '038071.mp3'),
(4042, 23, 457, 38, 'Ṣād', 'صٓ', 7, 8, 72, 'فَإِذَا سَوَّيۡتُهُۥ وَنَفَخۡتُ فِيهِ مِن رُّوحِي فَقَعُواْ لَهُۥ سَٰجِدِينَ ﱇ', 'فإذا سويته ونفخت فيه من روحي فقعوا له ساجدين', '038072.mp3'),
(4043, 23, 457, 38, 'Ṣād', 'صٓ', 8, 9, 73, 'فَسَجَدَ ٱلۡمَلَٰٓئِكَةُ كُلُّهُمۡ أَجۡمَعُونَ ﱈ', 'فسجد الملائكة كلهم أجمعون', '038073.mp3'),
(4044, 23, 457, 38, 'Ṣād', 'صٓ', 9, 9, 74, 'إِلَّآ إِبۡلِيسَ ٱسۡتَكۡبَرَ وَكَانَ مِنَ ٱلۡكَٰفِرِينَ ﱉ', 'إلا إبليس استكبر وكان من الكافرين', '038074.mp3'),
(4045, 23, 457, 38, 'Ṣād', 'صٓ', 9, 11, 75, 'قَالَ يَٰٓإِبۡلِيسُ مَا مَنَعَكَ أَن تَسۡجُدَ لِمَا خَلَقۡتُ بِيَدَيَّۖ أَسۡتَكۡبَرۡتَ أَمۡ كُنتَ مِنَ ٱلۡعَالِينَ ﱊ', 'قال ياإبليس ما منعك أن تسجد لما خلقت بيدي أستكبرت أم كنت من العالين', '038075.mp3'),
(4046, 23, 457, 38, 'Ṣād', 'صٓ', 11, 12, 76, 'قَالَ أَنَا۠ خَيۡرٞ مِّنۡهُ خَلَقۡتَنِي مِن نَّارٖ وَخَلَقۡتَهُۥ مِن طِينٖ ﱋ', 'قال أنا خير منه خلقتني من نار وخلقته من طين', '038076.mp3'),
(4047, 23, 457, 38, 'Ṣād', 'صٓ', 12, 12, 77, 'قَالَ فَٱخۡرُجۡ مِنۡهَا فَإِنَّكَ رَجِيمٞ ﱌ', 'قال فاخرج منها فإنك رجيم', '038077.mp3'),
(4048, 23, 457, 38, 'Ṣād', 'صٓ', 12, 13, 78, 'وَإِنَّ عَلَيۡكَ لَعۡنَتِيٓ إِلَىٰ يَوۡمِ ٱلدِّينِ ﱍ', 'وإن عليك لعنتي إلى يوم الدين', '038078.mp3'),
(4049, 23, 457, 38, 'Ṣād', 'صٓ', 13, 13, 79, 'قَالَ رَبِّ فَأَنظِرۡنِيٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ ﱎ', 'قال رب فأنظرني إلى يوم يبعثون', '038079.mp3'),
(4050, 23, 457, 38, 'Ṣād', 'صٓ', 13, 14, 80, 'قَالَ فَإِنَّكَ مِنَ ٱلۡمُنظَرِينَ ﱏ', 'قال فإنك من المنظرين', '038080.mp3'),
(4051, 23, 457, 38, 'Ṣād', 'صٓ', 14, 14, 81, 'إِلَىٰ يَوۡمِ ٱلۡوَقۡتِ ٱلۡمَعۡلُومِ ﱐ', 'إلى يوم الوقت المعلوم', '038081.mp3'),
(4052, 23, 457, 38, 'Ṣād', 'صٓ', 14, 15, 82, 'قَالَ فَبِعِزَّتِكَ لَأُغۡوِيَنَّهُمۡ أَجۡمَعِينَ ﱑ', 'قال فبعزتك لأغوينهم أجمعين', '038082.mp3'),
(4053, 23, 457, 38, 'Ṣād', 'صٓ', 15, 15, 83, 'إِلَّا عِبَادَكَ مِنۡهُمُ ٱلۡمُخۡلَصِينَ ﱒ', 'إلا عبادك منهم المخلصين', '038083.mp3'),
(4054, 23, 458, 38, 'Ṣād', 'صٓ', 1, 1, 84, 'قَالَ فَٱلۡحَقُّ وَٱلۡحَقَّ أَقُولُ ﱓ', 'قال فالحق والحق أقول', '038084.mp3'),
(4055, 23, 458, 38, 'Ṣād', 'صٓ', 1, 2, 85, 'لَأَمۡلَأَنَّ جَهَنَّمَ مِنكَ وَمِمَّن تَبِعَكَ مِنۡهُمۡ أَجۡمَعِينَ ﱔ', 'لأملأن جهنم منك وممن تبعك منهم أجمعين', '038085.mp3'),
(4056, 23, 458, 38, 'Ṣād', 'صٓ', 2, 2, 86, 'قُلۡ مَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٖ وَمَآ أَنَا۠ مِنَ ٱلۡمُتَكَلِّفِينَ ﱕ', 'قل ما أسألكم عليه من أجر وما أنا من المتكلفين', '038086.mp3'),
(4057, 23, 458, 38, 'Ṣād', 'صٓ', 3, 3, 87, 'إِنۡ هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ ﱖ', 'إن هو إلا ذكر للعالمين', '038087.mp3'),
(4058, 23, 458, 38, 'Ṣād', 'صٓ', 3, 3, 88, 'وَلَتَعۡلَمُنَّ نَبَأَهُۥ بَعۡدَ حِينِۭ ﱗ', 'ولتعلمن نبأه بعد حين', '038088.mp3'),
(4059, 23, 458, 39, 'Az-Zumar', 'الزُّمَر', 6, 6, 1, 'تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ ﰀ', 'تنزيل الكتاب من الله العزيز الحكيم', '039001.mp3'),
(4060, 23, 458, 39, 'Az-Zumar', 'الزُّمَر', 6, 7, 2, 'إِنَّآ أَنزَلۡنَآ إِلَيۡكَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ فَٱعۡبُدِ ٱللَّهَ مُخۡلِصٗا لَّهُ ٱلدِّينَ ﰁ', 'إنا أنزلنا إليك الكتاب بالحق فاعبد الله مخلصا له الدين', '039002.mp3'),
(4061, 23, 458, 39, 'Az-Zumar', 'الزُّمَر', 7, 11, 3, 'أَلَا لِلَّهِ ٱلدِّينُ ٱلۡخَالِصُۚ وَٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَ مَا نَعۡبُدُهُمۡ إِلَّا لِيُقَرِّبُونَآ إِلَى ٱللَّهِ زُلۡفَىٰٓ إِنَّ ٱللَّهَ يَحۡكُمُ بَيۡنَهُمۡ فِي مَا هُمۡ فِيهِ يَخۡتَلِفُونَۗ إِنَّ ٱللَّهَ لَا يَهۡدِي مَنۡ هُوَ كَٰذِبٞ كَفَّارٞ ﰂ', 'ألا لله الدين الخالص والذين اتخذوا من دونه أولياء ما نعبدهم إلا ليقربونا إلى الله زلفى إن الله يحكم بينهم في ما هم فيه يختلفون إن الله لا يهدي من هو كاذب كفار', '039003.mp3'),
(4062, 23, 458, 39, 'Az-Zumar', 'الزُّمَر', 11, 12, 4, 'لَّوۡ أَرَادَ ٱللَّهُ أَن يَتَّخِذَ وَلَدٗا لَّٱصۡطَفَىٰ مِمَّا يَخۡلُقُ مَا يَشَآءُۚ سُبۡحَٰنَهُۥۖ هُوَ ٱللَّهُ ٱلۡوَٰحِدُ ٱلۡقَهَّارُ ﰃ', 'لو أراد الله أن يتخذ ولدا لاصطفى مما يخلق ما يشاء سبحانه هو الله الواحد القهار', '039004.mp3'),
(4063, 23, 458, 39, 'Az-Zumar', 'الزُّمَر', 13, 15, 5, 'خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۖ يُكَوِّرُ ٱلَّيۡلَ عَلَى ٱلنَّهَارِ وَيُكَوِّرُ ٱلنَّهَارَ عَلَى ٱلَّيۡلِۖ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِي لِأَجَلٖ مُّسَمًّىۗ أَلَا هُوَ ٱلۡعَزِيزُ ٱلۡغَفَّٰرُ ﰄ', 'خلق السموات والأرض بالحق يكور الليل على النهار ويكور النهار على الليل وسخر الشمس والقمر كل يجري لأجل مسمى ألا هو العزيز الغفار', '039005.mp3'),
(4064, 23, 459, 39, 'Az-Zumar', 'الزُّمَر', 1, 4, 6, 'خَلَقَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ ثُمَّ جَعَلَ مِنۡهَا زَوۡجَهَا وَأَنزَلَ لَكُم مِّنَ ٱلۡأَنۡعَٰمِ ثَمَٰنِيَةَ أَزۡوَٰجٖۚ يَخۡلُقُكُمۡ فِي بُطُونِ أُمَّهَٰتِكُمۡ خَلۡقٗا مِّنۢ بَعۡدِ خَلۡقٖ فِي ظُلُمَٰتٖ ثَلَٰثٖۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡ لَهُ ٱلۡمُلۡكُۖ لَآ إِلَٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُصۡرَفُونَ ﰅ', 'خلقكم من نفس واحدة ثم جعل منها زوجها وأنزل لكم من الأنعام ثمانية أزواج يخلقكم في بطون أمهاتكم خلقا من بعد خلق في ظلمات ثلاث ذلكم الله ربكم له الملك لا إله إلا هو فأنى تصرفون', '039006.mp3'),
(4065, 23, 459, 39, 'Az-Zumar', 'الزُّمَر', 4, 7, 7, 'إِن تَكۡفُرُواْ فَإِنَّ ٱللَّهَ غَنِيٌّ عَنكُمۡۖ وَلَا يَرۡضَىٰ لِعِبَادِهِ ٱلۡكُفۡرَۖ وَإِن تَشۡكُرُواْ يَرۡضَهُ لَكُمۡۗ وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۚ ثُمَّ إِلَىٰ رَبِّكُم مَّرۡجِعُكُمۡ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ ﰆ', 'إن تكفروا فإن الله غني عنكم ولا يرضى لعباده الكفر وإن تشكروا يرضه لكم ولا تزر وازرة وزر أخرى ثم إلى ربكم مرجعكم فينبئكم بما كنتم تعملون إنه عليم بذات الصدور', '039007.mp3'),
(4066, 23, 459, 39, 'Az-Zumar', 'الزُّمَر', 8, 10, 8, '۞ وَإِذَا مَسَّ ٱلۡإِنسَٰنَ ضُرّٞ دَعَا رَبَّهُۥ مُنِيبًا إِلَيۡهِ ثُمَّ إِذَا خَوَّلَهُۥ نِعۡمَةٗ مِّنۡهُ نَسِيَ مَا كَانَ يَدۡعُوٓاْ إِلَيۡهِ مِن قَبۡلُ وَجَعَلَ لِلَّهِ أَندَادٗا لِّيُضِلَّ عَن سَبِيلِهِۦۚ قُلۡ تَمَتَّعۡ بِكُفۡرِكَ قَلِيلًا إِنَّكَ مِنۡ أَصۡحَٰبِ ٱلنَّارِ ﰇ', 'وإذا مس الإنسان ضر دعا ربه منيبا إليه ثم إذا خوله نعمة منه نسي ما كان يدعو إليه من قبل وجعل لله أندادا ليضل عن سبيله قل تمتع بكفرك قليلا إنك من أصحاب النار', '039008.mp3'),
