(5583, 29, 578, 75, 'Al-Qiyāmah', 'القِيَامة', 5, 5, 32, 'وَلَٰكِن كَذَّبَ وَتَوَلَّىٰ ﰟ', 'ولكن كذب وتولى', '075032.mp3'),
(5584, 29, 578, 75, 'Al-Qiyāmah', 'القِيَامة', 5, 6, 33, 'ثُمَّ ذَهَبَ إِلَىٰٓ أَهۡلِهِۦ يَتَمَطَّىٰٓ ﰠ', 'ثم ذهب إلى أهله يتمطى', '075033.mp3'),
(5585, 29, 578, 75, 'Al-Qiyāmah', 'القِيَامة', 6, 6, 34, 'أَوۡلَىٰ لَكَ فَأَوۡلَىٰ ﰡ', 'أولى لك فأولى', '075034.mp3'),
(5586, 29, 578, 75, 'Al-Qiyāmah', 'القِيَامة', 6, 6, 35, 'ثُمَّ أَوۡلَىٰ لَكَ فَأَوۡلَىٰٓ ﰢ', 'ثم أولى لك فأولى', '075035.mp3'),
(5587, 29, 578, 75, 'Al-Qiyāmah', 'القِيَامة', 6, 7, 36, 'أَيَحۡسَبُ ٱلۡإِنسَٰنُ أَن يُتۡرَكَ سُدًى ﰣ', 'أيحسب الإنسان أن يترك سدى', '075036.mp3'),
(5588, 29, 578, 75, 'Al-Qiyāmah', 'القِيَامة', 7, 7, 37, 'أَلَمۡ يَكُ نُطۡفَةٗ مِّن مَّنِيّٖ يُمۡنَىٰ ﰤ', 'ألم يك نطفة من مني يمنى', '075037.mp3'),
(5589, 29, 578, 75, 'Al-Qiyāmah', 'القِيَامة', 8, 8, 38, 'ثُمَّ كَانَ عَلَقَةٗ فَخَلَقَ فَسَوَّىٰ ﰥ', 'ثم كان علقة فخلق فسوى', '075038.mp3'),
(5590, 29, 578, 75, 'Al-Qiyāmah', 'القِيَامة', 8, 9, 39, 'فَجَعَلَ مِنۡهُ ٱلزَّوۡجَيۡنِ ٱلذَّكَرَ وَٱلۡأُنثَىٰٓ ﰦ', 'فجعل منه الزوجين الذكر والأنثى', '075039.mp3');
INSERT INTO `quran_hafs` (`id`, `jozz`, `page`, `sura_no`, `sura_name_en`, `sura_name_ar`, `line_start`, `line_end`, `aya_no`, `aya_text`, `aya_text_emlaey`, `sound`) VALUES
(5591, 29, 578, 75, 'Al-Qiyāmah', 'القِيَامة', 9, 9, 40, 'أَلَيۡسَ ذَٰلِكَ بِقَٰدِرٍ عَلَىٰٓ أَن يُحۡـِۧيَ ٱلۡمَوۡتَىٰ ﰧ', 'أليس ذلك بقادر على أن يحيي الموتى', '075040.mp3'),
(5592, 29, 578, 76, 'Al-Insān', 'الإنسَان', 12, 12, 1, 'هَلۡ أَتَىٰ عَلَى ٱلۡإِنسَٰنِ حِينٞ مِّنَ ٱلدَّهۡرِ لَمۡ يَكُن شَيۡـٔٗا مَّذۡكُورًا ﰀ', 'هل أتى على الإنسان حين من الدهر لم يكن شيئا مذكورا', '076001.mp3'),
(5593, 29, 578, 76, 'Al-Insān', 'الإنسَان', 12, 13, 2, 'إِنَّا خَلَقۡنَا ٱلۡإِنسَٰنَ مِن نُّطۡفَةٍ أَمۡشَاجٖ نَّبۡتَلِيهِ فَجَعَلۡنَٰهُ سَمِيعَۢا بَصِيرًا ﰁ', 'إنا خلقنا الإنسان من نطفة أمشاج نبتليه فجعلناه سميعا بصيرا', '076002.mp3'),
(5594, 29, 578, 76, 'Al-Insān', 'الإنسَان', 13, 14, 3, 'إِنَّا هَدَيۡنَٰهُ ٱلسَّبِيلَ إِمَّا شَاكِرٗا وَإِمَّا كَفُورًا ﰂ', 'إنا هديناه السبيل إما شاكرا وإما كفورا', '076003.mp3'),
(5595, 29, 578, 76, 'Al-Insān', 'الإنسَان', 14, 15, 4, 'إِنَّآ أَعۡتَدۡنَا لِلۡكَٰفِرِينَ سَلَٰسِلَاْ وَأَغۡلَٰلٗا وَسَعِيرًا ﰃ', 'إنا أعتدنا للكافرين سلاسل وأغلالا وسعيرا', '076004.mp3'),
(5596, 29, 578, 76, 'Al-Insān', 'الإنسَان', 15, 15, 5, 'إِنَّ ٱلۡأَبۡرَارَ يَشۡرَبُونَ مِن كَأۡسٖ كَانَ مِزَاجُهَا كَافُورًا ﰄ', 'إن الأبرار يشربون من كأس كان مزاجها كافورا', '076005.mp3'),
(5597, 29, 579, 76, 'Al-Insān', 'الإنسَان', 1, 1, 6, 'عَيۡنٗا يَشۡرَبُ بِهَا عِبَادُ ٱللَّهِ يُفَجِّرُونَهَا تَفۡجِيرٗا ﰅ', 'عينا يشرب بها عباد الله يفجرونها تفجيرا', '076006.mp3'),
(5598, 29, 579, 76, 'Al-Insān', 'الإنسَان', 1, 2, 7, 'يُوفُونَ بِٱلنَّذۡرِ وَيَخَافُونَ يَوۡمٗا كَانَ شَرُّهُۥ مُسۡتَطِيرٗا ﰆ', 'يوفون بالنذر ويخافون يوما كان شره مستطيرا', '076007.mp3'),
(5599, 29, 579, 76, 'Al-Insān', 'الإنسَان', 2, 3, 8, 'وَيُطۡعِمُونَ ٱلطَّعَامَ عَلَىٰ حُبِّهِۦ مِسۡكِينٗا وَيَتِيمٗا وَأَسِيرًا ﰇ', 'ويطعمون الطعام على حبه مسكينا ويتيما وأسيرا', '076008.mp3'),
(5600, 29, 579, 76, 'Al-Insān', 'الإنسَان', 3, 4, 9, 'إِنَّمَا نُطۡعِمُكُمۡ لِوَجۡهِ ٱللَّهِ لَا نُرِيدُ مِنكُمۡ جَزَآءٗ وَلَا شُكُورًا ﰈ', 'إنما نطعمكم لوجه الله لا نريد منكم جزاء ولا شكورا', '076009.mp3'),
(5601, 29, 579, 76, 'Al-Insān', 'الإنسَان', 4, 4, 10, 'إِنَّا نَخَافُ مِن رَّبِّنَا يَوۡمًا عَبُوسٗا قَمۡطَرِيرٗا ﰉ', 'إنا نخاف من ربنا يوما عبوسا قمطريرا', '076010.mp3'),
(5602, 29, 579, 76, 'Al-Insān', 'الإنسَان', 4, 5, 11, 'فَوَقَىٰهُمُ ٱللَّهُ شَرَّ ذَٰلِكَ ٱلۡيَوۡمِ وَلَقَّىٰهُمۡ نَضۡرَةٗ وَسُرُورٗا ﰊ', 'فوقاهم الله شر ذلك اليوم ولقاهم نضرة وسرورا', '076011.mp3'),
(5603, 29, 579, 76, 'Al-Insān', 'الإنسَان', 5, 6, 12, 'وَجَزَىٰهُم بِمَا صَبَرُواْ جَنَّةٗ وَحَرِيرٗا ﰋ', 'وجزاهم بما صبروا جنة وحريرا', '076012.mp3'),
(5604, 29, 579, 76, 'Al-Insān', 'الإنسَان', 6, 7, 13, 'مُّتَّكِـِٔينَ فِيهَا عَلَى ٱلۡأَرَآئِكِۖ لَا يَرَوۡنَ فِيهَا شَمۡسٗا وَلَا زَمۡهَرِيرٗا ﰌ', 'متكئين فيها على الأرائك لا يرون فيها شمسا ولا زمهريرا', '076013.mp3'),
(5605, 29, 579, 76, 'Al-Insān', 'الإنسَان', 7, 7, 14, 'وَدَانِيَةً عَلَيۡهِمۡ ظِلَٰلُهَا وَذُلِّلَتۡ قُطُوفُهَا تَذۡلِيلٗا ﰍ', 'ودانية عليهم ظلالها وذللت قطوفها تذليلا', '076014.mp3'),
(5606, 29, 579, 76, 'Al-Insān', 'الإنسَان', 8, 8, 15, 'وَيُطَافُ عَلَيۡهِم بِـَٔانِيَةٖ مِّن فِضَّةٖ وَأَكۡوَابٖ كَانَتۡ قَوَارِيرَا۠ ﰎ', 'ويطاف عليهم بآنية من فضة وأكواب كانت قواريرا', '076015.mp3'),
(5607, 29, 579, 76, 'Al-Insān', 'الإنسَان', 8, 9, 16, 'قَوَارِيرَاْ مِن فِضَّةٖ قَدَّرُوهَا تَقۡدِيرٗا ﰏ', 'قواريرا من فضة قدروها تقديرا', '076016.mp3'),
(5608, 29, 579, 76, 'Al-Insān', 'الإنسَان', 9, 9, 17, 'وَيُسۡقَوۡنَ فِيهَا كَأۡسٗا كَانَ مِزَاجُهَا زَنجَبِيلًا ﰐ', 'ويسقون فيها كأسا كان مزاجها زنجبيلا', '076017.mp3'),
(5609, 29, 579, 76, 'Al-Insān', 'الإنسَان', 9, 10, 18, 'عَيۡنٗا فِيهَا تُسَمَّىٰ سَلۡسَبِيلٗا ﰑ', 'عينا فيها تسمى سلسبيلا', '076018.mp3'),
(5610, 29, 579, 76, 'Al-Insān', 'الإنسَان', 10, 11, 19, '۞ وَيَطُوفُ عَلَيۡهِمۡ وِلۡدَٰنٞ مُّخَلَّدُونَ إِذَا رَأَيۡتَهُمۡ حَسِبۡتَهُمۡ لُؤۡلُؤٗا مَّنثُورٗا ﰒ', 'ويطوف عليهم ولدان مخلدون إذا رأيتهم حسبتهم لؤلؤا منثورا', '076019.mp3'),
(5611, 29, 579, 76, 'Al-Insān', 'الإنسَان', 11, 11, 20, 'وَإِذَا رَأَيۡتَ ثَمَّ رَأَيۡتَ نَعِيمٗا وَمُلۡكٗا كَبِيرًا ﰓ', 'وإذا رأيت ثم رأيت نعيما وملكا كبيرا', '076020.mp3'),
(5612, 29, 579, 76, 'Al-Insān', 'الإنسَان', 11, 13, 21, 'عَٰلِيَهُمۡ ثِيَابُ سُندُسٍ خُضۡرٞ وَإِسۡتَبۡرَقٞۖ وَحُلُّوٓاْ أَسَاوِرَ مِن فِضَّةٖ وَسَقَىٰهُمۡ رَبُّهُمۡ شَرَابٗا طَهُورًا ﰔ', 'عاليهم ثياب سندس خضر وإستبرق وحلوا أساور من فضة وسقاهم ربهم شرابا طهورا', '076021.mp3'),
(5613, 29, 579, 76, 'Al-Insān', 'الإنسَان', 13, 13, 22, 'إِنَّ هَٰذَا كَانَ لَكُمۡ جَزَآءٗ وَكَانَ سَعۡيُكُم مَّشۡكُورًا ﰕ', 'إن هذا كان لكم جزاء وكان سعيكم مشكورا', '076022.mp3'),
(5614, 29, 579, 76, 'Al-Insān', 'الإنسَان', 14, 14, 23, 'إِنَّا نَحۡنُ نَزَّلۡنَا عَلَيۡكَ ٱلۡقُرۡءَانَ تَنزِيلٗا ﰖ', 'إنا نحن نزلنا عليك القرآن تنزيلا', '076023.mp3'),
(5615, 29, 579, 76, 'Al-Insān', 'الإنسَان', 14, 15, 24, 'فَٱصۡبِرۡ لِحُكۡمِ رَبِّكَ وَلَا تُطِعۡ مِنۡهُمۡ ءَاثِمًا أَوۡ كَفُورٗا ﰗ', 'فاصبر لحكم ربك ولا تطع منهم آثما أو كفورا', '076024.mp3'),
(5616, 29, 579, 76, 'Al-Insān', 'الإنسَان', 15, 15, 25, 'وَٱذۡكُرِ ٱسۡمَ رَبِّكَ بُكۡرَةٗ وَأَصِيلٗا ﰘ', 'واذكر اسم ربك بكرة وأصيلا', '076025.mp3'),
(5617, 29, 580, 76, 'Al-Insān', 'الإنسَان', 1, 1, 26, 'وَمِنَ ٱلَّيۡلِ فَٱسۡجُدۡ لَهُۥ وَسَبِّحۡهُ لَيۡلٗا طَوِيلًا ﰙ', 'ومن الليل فاسجد له وسبحه ليلا طويلا', '076026.mp3'),
(5618, 29, 580, 76, 'Al-Insān', 'الإنسَان', 1, 2, 27, 'إِنَّ هَٰٓؤُلَآءِ يُحِبُّونَ ٱلۡعَاجِلَةَ وَيَذَرُونَ وَرَآءَهُمۡ يَوۡمٗا ثَقِيلٗا ﰚ', 'إن هؤلاء يحبون العاجلة ويذرون وراءهم يوما ثقيلا', '076027.mp3'),
(5619, 29, 580, 76, 'Al-Insān', 'الإنسَان', 2, 3, 28, 'نَّحۡنُ خَلَقۡنَٰهُمۡ وَشَدَدۡنَآ أَسۡرَهُمۡۖ وَإِذَا شِئۡنَا بَدَّلۡنَآ أَمۡثَٰلَهُمۡ تَبۡدِيلًا ﰛ', 'نحن خلقناهم وشددنا أسرهم وإذا شئنا بدلنا أمثالهم تبديلا', '076028.mp3'),
(5620, 29, 580, 76, 'Al-Insān', 'الإنسَان', 3, 4, 29, 'إِنَّ هَٰذِهِۦ تَذۡكِرَةٞۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلٗا ﰜ', 'إن هذه تذكرة فمن شاء اتخذ إلى ربه سبيلا', '076029.mp3'),
(5621, 29, 580, 76, 'Al-Insān', 'الإنسَان', 4, 5, 30, 'وَمَا تَشَآءُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُۚ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمٗا ﰝ', 'وما تشاءون إلا أن يشاء الله إن الله كان عليما حكيما', '076030.mp3'),
(5622, 29, 580, 76, 'Al-Insān', 'الإنسَان', 5, 6, 31, 'يُدۡخِلُ مَن يَشَآءُ فِي رَحۡمَتِهِۦۚ وَٱلظَّٰلِمِينَ أَعَدَّ لَهُمۡ عَذَابًا أَلِيمَۢا ﰞ', 'يدخل من يشاء في رحمته والظالمين أعد لهم عذابا أليما', '076031.mp3'),
(5623, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 9, 9, 1, 'وَٱلۡمُرۡسَلَٰتِ عُرۡفٗا ﰀ', 'والمرسلات عرفا', '077001.mp3'),
(5624, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 9, 9, 2, 'فَٱلۡعَٰصِفَٰتِ عَصۡفٗا ﰁ', 'فالعاصفات عصفا', '077002.mp3'),
(5625, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 9, 9, 3, 'وَٱلنَّٰشِرَٰتِ نَشۡرٗا ﰂ', 'والناشرات نشرا', '077003.mp3'),
(5626, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 10, 10, 4, 'فَٱلۡفَٰرِقَٰتِ فَرۡقٗا ﰃ', 'فالفارقات فرقا', '077004.mp3'),
(5627, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 10, 10, 5, 'فَٱلۡمُلۡقِيَٰتِ ذِكۡرًا ﰄ', 'فالملقيات ذكرا', '077005.mp3'),
(5628, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 10, 10, 6, 'عُذۡرًا أَوۡ نُذۡرًا ﰅ', 'عذرا أو نذرا', '077006.mp3'),
(5629, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 10, 11, 7, 'إِنَّمَا تُوعَدُونَ لَوَٰقِعٞ ﰆ', 'إنما توعدون لواقع', '077007.mp3'),
(5630, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 11, 11, 8, 'فَإِذَا ٱلنُّجُومُ طُمِسَتۡ ﰇ', 'فإذا النجوم طمست', '077008.mp3'),
(5631, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 11, 11, 9, 'وَإِذَا ٱلسَّمَآءُ فُرِجَتۡ ﰈ', 'وإذا السماء فرجت', '077009.mp3'),
(5632, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 12, 12, 10, 'وَإِذَا ٱلۡجِبَالُ نُسِفَتۡ ﰉ', 'وإذا الجبال نسفت', '077010.mp3'),
(5633, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 12, 12, 11, 'وَإِذَا ٱلرُّسُلُ أُقِّتَتۡ ﰊ', 'وإذا الرسل أقتت', '077011.mp3'),
(5634, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 12, 12, 12, 'لِأَيِّ يَوۡمٍ أُجِّلَتۡ ﰋ', 'لأي يوم أجلت', '077012.mp3'),
(5635, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 13, 13, 13, 'لِيَوۡمِ ٱلۡفَصۡلِ ﰌ', 'ليوم الفصل', '077013.mp3'),
(5636, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 13, 13, 14, 'وَمَآ أَدۡرَىٰكَ مَا يَوۡمُ ٱلۡفَصۡلِ ﰍ', 'وما أدراك ما يوم الفصل', '077014.mp3'),
(5637, 29, 580, 77, 'Al-Mursalāt', 'المُرسَلات', 13, 14, 15, 'وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ ﰎ', 'ويل يومئذ للمكذبين', '077015.mp3'),
